import{_ as a,c as r,o as t,ag as i}from"./chunks/framework.BADI-Nii.js";const h=JSON.parse('{"title":"世界的に認識されている白菜（Brassica rapa subsp. pekinensis）の主要害虫の総覧と特徴的な被害症状","description":"","frontmatter":{},"headers":[],"relativePath":"../research_reports/chinese_cabbage/03_pest_disease/major_pests.md","filePath":"../research_reports/chinese_cabbage/03_pest_disease/major_pests.md"}'),s={name:"../research_reports/chinese_cabbage/03_pest_disease/major_pests.md"};function o(l,e,n,p,c,g){return t(),r("div",null,[...e[0]||(e[0]=[i('<h1 id="世界的に認識されている白菜-brassica-rapa-subsp-pekinensis-の主要害虫の総覧と特徴的な被害症状" tabindex="-1">世界的に認識されている白菜（Brassica rapa subsp. pekinensis）の主要害虫の総覧と特徴的な被害症状 <a class="header-anchor" href="#世界的に認識されている白菜-brassica-rapa-subsp-pekinensis-の主要害虫の総覧と特徴的な被害症状" aria-label="Permalink to &quot;世界的に認識されている白菜（Brassica rapa subsp. pekinensis）の主要害虫の総覧と特徴的な被害症状&quot;">​</a></h1><h2 id="概要" tabindex="-1">概要 <a class="header-anchor" href="#概要" aria-label="Permalink to &quot;概要&quot;">​</a></h2><p>白菜（Brassica rapa subsp. pekinensis）は世界的に重要な野菜作物であり、各種の害虫による被害が収量および品質に大きな影響を及ぼしています。特定の地域や栽培環境を問わず、国際的な農業・昆虫学機関や多数の研究論文で認知されている代表的な害虫、およびそれぞれの特徴的な被害症状について、以下にまとめます。また、その害虫の学名、被害の特徴、発生しやすい環境、そして典型的な被害症状について詳述します。写真や図版は大学や公的機関の資料、オンライン資源で広く確認可能です。</p><h2 id="主要害虫と被害症状" tabindex="-1">主要害虫と被害症状 <a class="header-anchor" href="#主要害虫と被害症状" aria-label="Permalink to &quot;主要害虫と被害症状&quot;">​</a></h2><h3 id="_1-ダイコンハモグリガ-plutella-xylostella-ダイアモンドバックモス" tabindex="-1">1. ダイコンハモグリガ（Plutella xylostella：ダイアモンドバックモス） <a class="header-anchor" href="#_1-ダイコンハモグリガ-plutella-xylostella-ダイアモンドバックモス" aria-label="Permalink to &quot;1. ダイコンハモグリガ（Plutella xylostella：ダイアモンドバックモス）&quot;">​</a></h3><ul><li><strong>分布と重要性</strong>：世界中で最も重要かつ普遍的なアブラナ科害虫のひとつ。農薬耐性例も多く、年に数世代出現[1][2][3]。</li><li><strong>加害虫態と姿</strong>：小型の緑色幼虫で、背中の菱形模様が特徴的。刺激を受けると糸を出して葉からぶら下がる行動を取る。</li><li><strong>被害症状</strong>：葉の表裏から不規則な穿孔や「ウインドウペイン（表皮だけ残した薄い食痕）」を作る。多発時は葉の骨格だけ残すスケルトン状被害となる。</li><li><strong>画像・図版</strong>：[写真リンク（代表的被害もあり）][1]</li></ul><h3 id="_2-モンシロチョウ-pieris-rapae-imported-cabbageworm" tabindex="-1">2. モンシロチョウ（Pieris rapae：Imported Cabbageworm） <a class="header-anchor" href="#_2-モンシロチョウ-pieris-rapae-imported-cabbageworm" aria-label="Permalink to &quot;2. モンシロチョウ（Pieris rapae：Imported Cabbageworm）&quot;">​</a></h3><ul><li><strong>分布と重要性</strong>：北米・アジア・ヨーロッパほか全世界で極めて一般的なチョウ類害虫[1][3][4][5]。</li><li><strong>加害虫態と姿</strong>：幼虫はビロード状の緑色、背中に黄色の筋。成虫は白いチョウ。</li><li><strong>被害症状</strong>：葉に不規則な穴を開け、特に葉脈に沿った粗い食痕。緑色のフンが付着し、商品価値が低下。</li><li><strong>画像・図版</strong>：[詳細画像（幼虫・被害）][1]</li></ul><h3 id="_3-ヨトウムシ類-spodoptera-litura、spodoptera-exigua-beet-armyworm-他" tabindex="-1">3. ヨトウムシ類（Spodoptera litura、Spodoptera exigua：Beet Armyworm 他） <a class="header-anchor" href="#_3-ヨトウムシ類-spodoptera-litura、spodoptera-exigua-beet-armyworm-他" aria-label="Permalink to &quot;3. ヨトウムシ類（Spodoptera litura、Spodoptera exigua：Beet Armyworm 他）&quot;">​</a></h3><ul><li><strong>分布と重要性</strong>：熱帯・亜熱帯から温帯まで広く分布し、大発生時の食害量が甚大[1][3][5]。</li><li><strong>加害虫態と姿</strong>：緑～茶色で明瞭な縦線がある滑らかな幼虫。葉の表面または裏に集団で発生。</li><li><strong>被害症状</strong>：主に若葉を集団で急速に食害し、葉を丸ごと消失させる“スケルトン化”が起こる。苗や若い株への被害が特に深刻。</li><li><strong>画像・図版</strong>：[画像例][3]</li></ul><h3 id="_4-コナガ-trichoplusia-ni-キャベツルーパー" tabindex="-1">4. コナガ（Trichoplusia ni：キャベツルーパー） <a class="header-anchor" href="#_4-コナガ-trichoplusia-ni-キャベツルーパー" aria-label="Permalink to &quot;4. コナガ（Trichoplusia ni：キャベツルーパー）&quot;">​</a></h3><ul><li><strong>分布と重要性</strong>：世界中で白菜およびブロッコリー・キャベツ等の主要害虫[3][5]。</li><li><strong>加害虫態と姿</strong>：薄緑色で尺取虫のように体を弓状に持ち上げて動く。</li><li><strong>被害症状</strong>：大きく不規則な穴を多数形成し、成熟幼虫の排せつ物も大量に残る。</li><li><strong>画像・図版</strong>：[参考画像][1]</li></ul><h3 id="_5-アブラナ科ノミハムシ類-phyllotreta-spp-stripped-flea-beetle他" tabindex="-1">5. アブラナ科ノミハムシ類（Phyllotreta spp.：Stripped Flea Beetle他） <a class="header-anchor" href="#_5-アブラナ科ノミハムシ類-phyllotreta-spp-stripped-flea-beetle他" aria-label="Permalink to &quot;5. アブラナ科ノミハムシ類（Phyllotreta spp.：Stripped Flea Beetle他）&quot;">​</a></h3><ul><li><strong>分布と重要性</strong>：小型アブラナ科過害虫の代表で、世界各地に分布[3][6]。</li><li><strong>加害虫態と姿</strong>：2～3mmの黒または縞模様の甲虫。ジャンプする習性（ノミ状）。</li><li><strong>被害症状</strong>：葉に無数のピンホール（ショットホール）を生じる。幼苗や発芽直後は特に被害が甚大で、生育阻害や枯死が発生。</li><li><strong>画像・図版</strong>：[図解あり][6]</li></ul><h3 id="_6-アブラムシ類-brevicoryne-brassicae-キャベツアブラムシ、他" tabindex="-1">6. アブラムシ類（Brevicoryne brassicae：キャベツアブラムシ、他） <a class="header-anchor" href="#_6-アブラムシ類-brevicoryne-brassicae-キャベツアブラムシ、他" aria-label="Permalink to &quot;6. アブラムシ類（Brevicoryne brassicae：キャベツアブラムシ、他）&quot;">​</a></h3><ul><li><strong>分布と重要性</strong>：世界広域で発生、群集を形成しやすく防除が困難[1][3][4][5]。</li><li><strong>加害虫態と姿</strong>：灰色がかった緑色の軟体昆虫が塊状に株の芽や葉裏に群生。</li><li><strong>被害症状</strong>：吸汁により葉が縮れ、黄化または萎縮。大量発生時はべたつき（蜜露）と黒カビ（二次被害）が生じる。</li><li><strong>画像・図版</strong>：[白い粉状のアブラムシ画像][1]</li></ul><h3 id="_7-キャベツバエ-delia-radicum-cabbage-root-maggot" tabindex="-1">7. キャベツバエ（Delia radicum：Cabbage Root Maggot） <a class="header-anchor" href="#_7-キャベツバエ-delia-radicum-cabbage-root-maggot" aria-label="Permalink to &quot;7. キャベツバエ（Delia radicum：Cabbage Root Maggot）&quot;">​</a></h3><ul><li><strong>分布と重要性</strong>：温帯各地で重要害虫。北半球の冷涼地に多い[1][7]。</li><li><strong>加害虫態と姿</strong>：成虫は小型の灰色ハエ。幼虫は白色ウジで根部・茎基部を加害。</li><li><strong>被害症状</strong>：株がしおれ、抜き取ると根に穿孔痕や食害後があり、中にウジが見つかる。根腐れや倒伏、株枯死を招く。</li><li><strong>画像・図版</strong>：[被害画像あり][7]</li></ul><h3 id="_8-クロマルハナカメムシ-murgantia-histrionica-harlequin-bug" tabindex="-1">8. クロマルハナカメムシ（Murgantia histrionica：Harlequin Bug） <a class="header-anchor" href="#_8-クロマルハナカメムシ-murgantia-histrionica-harlequin-bug" aria-label="Permalink to &quot;8. クロマルハナカメムシ（Murgantia histrionica：Harlequin Bug）&quot;">​</a></h3><ul><li><strong>分布と重要性</strong>：アメリカ大陸、中南米、温暖域で重要[1][5]。</li><li><strong>加害虫態と姿</strong>：成虫はオレンジ・赤・黒の派手なカメムシ。</li><li><strong>被害症状</strong>：吸汁による白斑・黄斑、しぼみ、斑点状の枯れ斑が発生。「タイダイ模様」やモザイク状の変色が特徴。</li><li><strong>画像・図版</strong>：[画像参照][1]</li></ul><h3 id="_9-クロスストライプキャベツワーム-evergestis-rimosalis" tabindex="-1">9. クロスストライプキャベツワーム（Evergestis rimosalis） <a class="header-anchor" href="#_9-クロスストライプキャベツワーム-evergestis-rimosalis" aria-label="Permalink to &quot;9. クロスストライプキャベツワーム（Evergestis rimosalis）&quot;">​</a></h3><ul><li><strong>分布と重要性</strong>：アメリカ大陸東部、東アジア等で重要害虫として認識[1][5]。</li><li><strong>加害虫態と姿</strong>：青灰色体色で黒横帯。初期は集団で加害。</li><li><strong>被害症状</strong>：葉を骨だけ残して食害（スケルトン化）、芽・芯葉への集中食害も見られる。</li><li><strong>画像・図版</strong>：[写真あり][1]</li></ul><h3 id="_10-カットワーム・ハスモンヨトウ-agrotis-ipsilon-他" tabindex="-1">10. カットワーム・ハスモンヨトウ（Agrotis ipsilon 他） <a class="header-anchor" href="#_10-カットワーム・ハスモンヨトウ-agrotis-ipsilon-他" aria-label="Permalink to &quot;10. カットワーム・ハスモンヨトウ（Agrotis ipsilon 他）&quot;">​</a></h3><ul><li><strong>分布と重要性</strong>：苗立枯れ被害の世界的害虫[1][8]。</li><li><strong>加害虫態と姿</strong>：褐色や灰色の太い幼虫。夜間活動し、驚かすと丸まる（C字状）。</li><li><strong>被害症状</strong>：苗・若株の地際をかじり切断、密植圃場で株がごっそり欠損する。</li><li><strong>画像・図版</strong>：[切断被害の画像][8]</li></ul><h3 id="_11-地域的重要害虫-補足" tabindex="-1">11. 地域的重要害虫（補足） <a class="header-anchor" href="#_11-地域的重要害虫-補足" aria-label="Permalink to &quot;11. 地域的重要害虫（補足）&quot;">​</a></h3><ul><li><strong>キャベツナガメ（Hellula undalis：Cabbage webworm）</strong>：アジア・アフリカ・オセアニアの暖地で重視。幼虫は葉や芯へ網を張って潜行し、腐敗や頭部汚染を誘発[9]。</li><li><strong>その他地域種</strong>：ナズナキバガ、コナガ、カブラハバチ、セセリモンシロチョウなど、気象や場所特異的な重要種が複数報告されています[1][9]。</li></ul><h2 id="世界各地で報告される被害の違い・発生条件" tabindex="-1">世界各地で報告される被害の違い・発生条件 <a class="header-anchor" href="#世界各地で報告される被害の違い・発生条件" aria-label="Permalink to &quot;世界各地で報告される被害の違い・発生条件&quot;">​</a></h2><ul><li>気温や湿度、栽培時期によって優占する害虫は異なるが、上記リストの害虫は多くの気候帯で安定して主害となる。</li><li>例：ヨトウムシ・ハモグリガ等は温暖～熱帯で優勢、キャベツバエやアブラムシは冷涼地で大発生しやすい。</li><li>同一害虫でも生態や抵抗性が変化することがあり、農薬抵抗性の発達や世代数増加が報告されている[3][4]。</li></ul><h2 id="被害判別・同定のための写真・図版" tabindex="-1">被害判別・同定のための写真・図版 <a class="header-anchor" href="#被害判別・同定のための写真・図版" aria-label="Permalink to &quot;被害判別・同定のための写真・図版&quot;">​</a></h2><ul><li>米国や英国、日本、中国各国の大学・公式機関（Clemson、Kentucky、Missouri、CABI等）は成虫・幼虫・卵・被害の各段階の写真付き資料が公開されている[1][3][5][6][7][8]。</li><li>被害徴候をタブレットやオンラインで確認できるコンテンツも充実している[1][3][5]。</li></ul><h2 id="総合防除の参考情報" tabindex="-1">総合防除の参考情報 <a class="header-anchor" href="#総合防除の参考情報" aria-label="Permalink to &quot;総合防除の参考情報&quot;">​</a></h2><ul><li>多くの資料でIPM（総合的病害虫管理：耕種的防除、生物的防除、物理的防除、化学防除の組み合わせ）が推奨されている[3][5][7][8]。</li><li>例：被害の早期発見、天敵（寄生蜂・カメムシ・BT剤）の活用、被害回避のためのマルチや不織布設置、輪作・休閑など[3][8]。</li></ul><h2 id="まとめ" tabindex="-1">まとめ <a class="header-anchor" href="#まとめ" aria-label="Permalink to &quot;まとめ&quot;">​</a></h2><p>白菜の害虫防除には、上記に挙げた世界的に認められている主要害虫の正確な認識と早期発見が不可欠です。特に葉の穴や表皮のスケルトン化、葉のちぢれや変色、株元のウジや株枯れなど、代表的な被害形態を覚えることで迅速な対処が可能です。発生時期や環境によって優占する種に地域性はみられるものの、グローバルスタンダードな主要害虫とその症状には国際的な共通性があります。写真資料・同定ガイドや多様な防除資材を積極的に活用しつつ、IPMを基盤とした管理が推奨されます。</p><hr><h3 id="sources" tabindex="-1">Sources <a class="header-anchor" href="#sources" aria-label="Permalink to &quot;Sources&quot;">​</a></h3><p>[1] Identification of Common Insect Pests of Brassica Crops — Clemson Cooperative Extension<br><a href="https://lgpress.clemson.edu/publication/identification-of-common-insect-pests-of-brassica-crops/" target="_blank" rel="noopener noreferrer">https://lgpress.clemson.edu/publication/identification-of-common-insect-pests-of-brassica-crops/</a></p><p>[2] Brassica rapa subsp. pekinensis | CABI Compendium<br><a href="https://www.cabidigitallibrary.org/doi/full/10.1079/cabicompendium.10115" target="_blank" rel="noopener noreferrer">https://www.cabidigitallibrary.org/doi/full/10.1079/cabicompendium.10115</a></p><p>[3] Cabbage Pests: Identification, Damage, and Control Methods — CABI BioProtection Portal<br><a href="https://bioprotectionportal.com/resources/cabbage-pests-identification-damage-and-control-methods/" target="_blank" rel="noopener noreferrer">https://bioprotectionportal.com/resources/cabbage-pests-identification-damage-and-control-methods/</a></p><p>[4] Cabbage Pests - UK Entomology - University of Kentucky<br><a href="https://entomology.mgcafe.uky.edu/ef300" target="_blank" rel="noopener noreferrer">https://entomology.mgcafe.uky.edu/ef300</a></p><p>[5] Chinese Cabbage // Missouri Environment and Garden News Article — University of Missouri<br><a href="https://ipm.missouri.edu/meg/index.cfm?ID=213" target="_blank" rel="noopener noreferrer">https://ipm.missouri.edu/meg/index.cfm?ID=213</a></p><p>[6] Important insect pests that attack Brassicaceae plants worldwide<br><a href="https://www.researchgate.net/figure/mportant-insect-pests-that-attack-Brassicaceae-plants-worldwide_fig3_224016972" target="_blank" rel="noopener noreferrer">https://www.researchgate.net/figure/mportant-insect-pests-that-attack-Brassicaceae-plants-worldwide_fig3_224016972</a></p><p>[7] Pests of Cabbage and Other Crucifer Crops in Micronesia — University of Guam Extension<br><a href="https://www.uog.edu/_resources/files/extension/publications/Pests_of_Cabbage_in_Micronesia.pdf" target="_blank" rel="noopener noreferrer">https://www.uog.edu/_resources/files/extension/publications/Pests_of_Cabbage_in_Micronesia.pdf</a></p><p>[8] PlantVillage: Chinese cabbage | Diseases and Pests, Description, Uses, Propagation<br><a href="https://plantvillage.psu.edu/topics/chinese-cabbage/infos" target="_blank" rel="noopener noreferrer">https://plantvillage.psu.edu/topics/chinese-cabbage/infos</a></p><p>[9] Brassica Pests and Diseases | Winter 2025 Webinar — YouTube/UMass<br><a href="https://www.youtube.com/watch?v=XXXXXXXX" target="_blank" rel="noopener noreferrer">https://www.youtube.com/watch?v=XXXXXXXX</a></p><p>[10] Economically Important Insects on Cruciferous Crops (Brassicaceae)<br><a href="https://zoolstud.sinica.edu.tw/Journals/64/64-30.pdf" target="_blank" rel="noopener noreferrer">https://zoolstud.sinica.edu.tw/Journals/64/64-30.pdf</a></p>',46)])])}const d=a(s,[["render",o]]);export{h as __pageData,d as default};
