import{_ as e,c as l,o as i,ag as r}from"./chunks/framework.BADI-Nii.js";const p=JSON.parse('{"title":"世界のジャガイモ主要害虫と特徴的被害症状の総覧","description":"","frontmatter":{},"headers":[],"relativePath":"../research_reports/potato/03_pest_disease/major_pests.md","filePath":"../research_reports/potato/03_pest_disease/major_pests.md"}'),t={name:"../research_reports/potato/03_pest_disease/major_pests.md"};function o(h,a,s,n,u,d){return i(),l("div",null,[...a[0]||(a[0]=[r('<h1 id="世界のジャガイモ主要害虫と特徴的被害症状の総覧" tabindex="-1">世界のジャガイモ主要害虫と特徴的被害症状の総覧 <a class="header-anchor" href="#世界のジャガイモ主要害虫と特徴的被害症状の総覧" aria-label="Permalink to &quot;世界のジャガイモ主要害虫と特徴的被害症状の総覧&quot;">​</a></h1><h2 id="ジャガイモにおける主要害虫の総説" tabindex="-1">ジャガイモにおける主要害虫の総説 <a class="header-anchor" href="#ジャガイモにおける主要害虫の総説" aria-label="Permalink to &quot;ジャガイモにおける主要害虫の総説&quot;">​</a></h2><p>ジャガイモ（Solanum tuberosum）は、世界中で生産・消費される重要作物ですが、様々な害虫による被害が世界各地で問題となっています。その被害は収量の減少だけでなく、品質低下やウイルス病の介在など深刻な影響を及ぼします。本稿では、地域や栽培方式を問わず国際的に主要と認識されている代表的ジャガイモ害虫と、その現場で見られる特徴的な被害症状を、害虫ごとのライフステージや識別ポイントとも合わせて体系的に整理します。各項目には学名、日本語名、典型的な被害、加害ステージおよび主要な参照情報（日本語・英語）を示します。</p><hr><h2 id="_1-コロラドハムシ-colorado-potato-beetle-leptinotarsa-decemlineata" tabindex="-1">1. コロラドハムシ（Colorado Potato Beetle, Leptinotarsa decemlineata） <a class="header-anchor" href="#_1-コロラドハムシ-colorado-potato-beetle-leptinotarsa-decemlineata" aria-label="Permalink to &quot;1. コロラドハムシ（Colorado Potato Beetle, Leptinotarsa decemlineata）&quot;">​</a></h2><h3 id="加害の特性と被害症状" tabindex="-1">加害の特性と被害症状 <a class="header-anchor" href="#加害の特性と被害症状" aria-label="Permalink to &quot;加害の特性と被害症状&quot;">​</a></h3><ul><li>成虫は黄色地に10本の黒い縦縞が特徴で、葉裏にオレンジ色の卵塊を産み付ける。</li><li>幼虫は赤〜オレンジ色で体側に黒点が2列並び、葉に群れをなして食害する[1][2][3][4]。</li><li>葉を集中的に食害し、ひどい場合は全葉が食い尽くされ、株が枯死する。</li><li>特に開花期に最大の葉被害が現れる。</li></ul><h3 id="加害ステージ" tabindex="-1">加害ステージ <a class="header-anchor" href="#加害ステージ" aria-label="Permalink to &quot;加害ステージ&quot;">​</a></h3><ul><li>成虫と（特に）3齢、4齢の幼虫が葉を激しく食害[1][2]。</li></ul><h3 id="現場での識別・診断" tabindex="-1">現場での識別・診断 <a class="header-anchor" href="#現場での識別・診断" aria-label="Permalink to &quot;現場での識別・診断&quot;">​</a></h3><ul><li>ストライプ模様の成虫・鮮やかな卵塊・赤い幼虫を葉や葉裏で発見できる。</li><li>多世代発生（地域によって年2-3回）に注意。</li></ul><h3 id="日本語情報" tabindex="-1">日本語情報 <a class="header-anchor" href="#日本語情報" aria-label="Permalink to &quot;日本語情報&quot;">​</a></h3><ul><li>「テントウムシダマシ」とも呼ばれるが、類似種（28紋テントウムシなど）を含む地域表現もある[2][3]。</li></ul><hr><h2 id="_2-アブラムシ類-aphids-主にワタアブラムシ-myzus-persicae-ナスアブラムシ-macrosiphum-euphorbiae" tabindex="-1">2. アブラムシ類（Aphids; 主にワタアブラムシ: Myzus persicae, ナスアブラムシ: Macrosiphum euphorbiae） <a class="header-anchor" href="#_2-アブラムシ類-aphids-主にワタアブラムシ-myzus-persicae-ナスアブラムシ-macrosiphum-euphorbiae" aria-label="Permalink to &quot;2. アブラムシ類（Aphids; 主にワタアブラムシ: Myzus persicae, ナスアブラムシ: Macrosiphum euphorbiae）&quot;">​</a></h2><h3 id="加害の特性と被害症状-1" tabindex="-1">加害の特性と被害症状 <a class="header-anchor" href="#加害の特性と被害症状-1" aria-label="Permalink to &quot;加害の特性と被害症状&quot;">​</a></h3><ul><li>小型で軟体の昆虫。新葉・茎の裏側で群生加害する[2][3][4][5]。</li><li>吸汁により葉が黄化・縮れ・変形する。</li><li>ウイルス（特にPotato Virus Y, ジャガイモシワ病等）を媒介し、葉のモザイク、ちぢれ、斑紋などが広範囲に発生[3][4][5]。</li></ul><h3 id="加害ステージ-1" tabindex="-1">加害ステージ <a class="header-anchor" href="#加害ステージ-1" aria-label="Permalink to &quot;加害ステージ&quot;">​</a></h3><ul><li>幼虫・成虫とも吸汁・ウイルス媒介により被害を及ぼす[2][4]。</li></ul><h3 id="現場での識別・診断-1" tabindex="-1">現場での識別・診断 <a class="header-anchor" href="#現場での識別・診断-1" aria-label="Permalink to &quot;現場での識別・診断&quot;">​</a></h3><ul><li>頂点や葉裏での群集・べたついた蜜露・すす病の発生で鑑別。</li><li>モザイク症状や大規模な黄化群落を確認[4][5]。</li></ul><h3 id="日本語情報-1" tabindex="-1">日本語情報 <a class="header-anchor" href="#日本語情報-1" aria-label="Permalink to &quot;日本語情報&quot;">​</a></h3><ul><li>「アブラムシ」被害の存在は全国的に認識・早期駆除やウイルス防除の重要性も強調される[2][3][5]。</li></ul><hr><h2 id="_3-ジャガイモガ-テシアガ-potato-tuber-moth-phthorimaea-operculella-tecia-solanivora" tabindex="-1">3. ジャガイモガ/テシアガ（Potato Tuber Moth, Phthorimaea operculella / Tecia solanivora） <a class="header-anchor" href="#_3-ジャガイモガ-テシアガ-potato-tuber-moth-phthorimaea-operculella-tecia-solanivora" aria-label="Permalink to &quot;3. ジャガイモガ/テシアガ（Potato Tuber Moth, Phthorimaea operculella / Tecia solanivora）&quot;">​</a></h2><h3 id="加害の特性と被害症状-2" tabindex="-1">加害の特性と被害症状 <a class="header-anchor" href="#加害の特性と被害症状-2" aria-label="Permalink to &quot;加害の特性と被害症状&quot;">​</a></h3><ul><li>成虫は灰褐色小型蛾。葉、茎、露出した塊茎に卵を産む[1][3][6][2]。</li><li>幼虫（白～淡紅色）が葉裏・茎内を食害したり、土中や倉庫でイモ類に穿孔してトンネルやフラス（粉状排泄物）を残す。</li><li>イモは細長いトンネル、穴、しわ・萎縮・腐敗を伴い食用・種用不適となる。</li></ul><h3 id="加害ステージ-2" tabindex="-1">加害ステージ <a class="header-anchor" href="#加害ステージ-2" aria-label="Permalink to &quot;加害ステージ&quot;">​</a></h3><ul><li>幼虫が葉、茎、特に塊茎を強く加害[1][6]。</li></ul><h3 id="現場での識別・診断-2" tabindex="-1">現場での識別・診断 <a class="header-anchor" href="#現場での識別・診断-2" aria-label="Permalink to &quot;現場での識別・診断&quot;">​</a></h3><ul><li>イモの内部に曲がりくねった虫食い跡と排泄物。</li><li>葉巻きや葉裏の食痕も特徴。</li><li>Tecia solanivoraは特にラテンアメリカ・カナリア諸島等で問題[1][6]。</li></ul><h3 id="日本語情報-2" tabindex="-1">日本語情報 <a class="header-anchor" href="#日本語情報-2" aria-label="Permalink to &quot;日本語情報&quot;">​</a></h3><ul><li>「ジャガイモガ」として写真や症状詳細が複数の農業系ハンドブックで紹介[3][5]。</li></ul><hr><h2 id="_4-コガネムシ幼虫-ネキリムシ類-wireworms-agriotes-spp-limonius-spp-など" tabindex="-1">4. コガネムシ幼虫／ネキリムシ類（Wireworms: Agriotes spp., Limonius spp., など） <a class="header-anchor" href="#_4-コガネムシ幼虫-ネキリムシ類-wireworms-agriotes-spp-limonius-spp-など" aria-label="Permalink to &quot;4. コガネムシ幼虫／ネキリムシ類（Wireworms: Agriotes spp., Limonius spp., など）&quot;">​</a></h2><h3 id="加害の特性と被害症状-3" tabindex="-1">加害の特性と被害症状 <a class="header-anchor" href="#加害の特性と被害症状-3" aria-label="Permalink to &quot;加害の特性と被害症状&quot;">​</a></h3><ul><li>地中で発見される細長く堅い黄褐色の幼虫。新規耕作地や牧草跡に多発[2][3][4]。</li><li>ジャガイモ塊茎や種イモにトンネル・穴、表面の溝が残る。品質大幅低下。</li></ul><h3 id="加害ステージ-3" tabindex="-1">加害ステージ <a class="header-anchor" href="#加害ステージ-3" aria-label="Permalink to &quot;加害ステージ&quot;">​</a></h3><ul><li>幼虫（Wireworm）が主に塊茎や根部を食い荒らす[2][4]。</li></ul><h3 id="現場での識別・診断-3" tabindex="-1">現場での識別・診断 <a class="header-anchor" href="#現場での識別・診断-3" aria-label="Permalink to &quot;現場での識別・診断&quot;">​</a></h3><ul><li>イモ断面に長いまっすぐな穴、腐敗誘因となる。</li><li>新規開墾や牧草地からの転作圃場で発生リスク大[2][3]。</li></ul><h3 id="日本語情報-3" tabindex="-1">日本語情報 <a class="header-anchor" href="#日本語情報-3" aria-label="Permalink to &quot;日本語情報&quot;">​</a></h3><ul><li>「ネキリムシ」や「コガネムシ幼虫」として被害・対策が記述[2][3]。</li></ul><hr><h2 id="_5-ノミハムシ類-flea-beetles-epitrix-spp" tabindex="-1">5. ノミハムシ類（Flea Beetles: Epitrix spp.） <a class="header-anchor" href="#_5-ノミハムシ類-flea-beetles-epitrix-spp" aria-label="Permalink to &quot;5. ノミハムシ類（Flea Beetles: Epitrix spp.）&quot;">​</a></h2><h3 id="加害の特性と被害症状-4" tabindex="-1">加害の特性と被害症状 <a class="header-anchor" href="#加害の特性と被害症状-4" aria-label="Permalink to &quot;加害の特性と被害症状&quot;">​</a></h3><ul><li>1-3mm程度の小型黒色系の甲虫で、触れると跳ねて逃げる[4][2][3]。</li><li>葉に小さな丸い穴（ショットホール状）を集中して空ける。初期生育期の欠株や収量低下要因。</li></ul><h3 id="加害ステージ-4" tabindex="-1">加害ステージ <a class="header-anchor" href="#加害ステージ-4" aria-label="Permalink to &quot;加害ステージ&quot;">​</a></h3><ul><li>成虫の食害による[4]。</li></ul><h3 id="現場での識別・診断-4" tabindex="-1">現場での識別・診断 <a class="header-anchor" href="#現場での識別・診断-4" aria-label="Permalink to &quot;現場での識別・診断&quot;">​</a></h3><ul><li>若葉の集中的「丸穴」・小傷がパッチ状に分布。</li><li>大発生の場合は生育抑制。</li></ul><h3 id="日本語情報-4" tabindex="-1">日本語情報 <a class="header-anchor" href="#日本語情報-4" aria-label="Permalink to &quot;日本語情報&quot;">​</a></h3><ul><li>「ノミハムシ」「テントウムシダマシ」と記されることもあり、葉穴被害の具体例が多数[2][3]。</li></ul><hr><h2 id="_6-ジャガイモヒゲナガアブラムシ-リーフホッパー類-potato-leafhopper-empoasca-fabae他" tabindex="-1">6. ジャガイモヒゲナガアブラムシ／リーフホッパー類（Potato Leafhopper: Empoasca fabae他） <a class="header-anchor" href="#_6-ジャガイモヒゲナガアブラムシ-リーフホッパー類-potato-leafhopper-empoasca-fabae他" aria-label="Permalink to &quot;6. ジャガイモヒゲナガアブラムシ／リーフホッパー類（Potato Leafhopper: Empoasca fabae他）&quot;">​</a></h2><h3 id="加害の特性と被害症状-5" tabindex="-1">加害の特性と被害症状 <a class="header-anchor" href="#加害の特性と被害症状-5" aria-label="Permalink to &quot;加害の特性と被害症状&quot;">​</a></h3><ul><li>約2-3mmのくさび形・薄緑色昆虫。驚かすとジャンプ。[4]</li><li>葉縁から黄変、カール、枯れ上がる「ホッパーバーン」現象が特徴。</li></ul><h3 id="加害ステージ-5" tabindex="-1">加害ステージ <a class="header-anchor" href="#加害ステージ-5" aria-label="Permalink to &quot;加害ステージ&quot;">​</a></h3><ul><li>幼虫・成虫の吸汁加害[4]。</li></ul><h3 id="現場での識別・診断-5" tabindex="-1">現場での識別・診断 <a class="header-anchor" href="#現場での識別・診断-5" aria-label="Permalink to &quot;現場での識別・診断&quot;">​</a></h3><ul><li>葉全体の縮れ、葉縁黄化、点状枯死が目立つ。</li><li>夏場、他作物からの飛来注意。</li></ul><hr><h2 id="_7-コナジラミ類-whiteflies-bemisia-tabaci-trialeurodes-vaporariorum" tabindex="-1">7. コナジラミ類（Whiteflies: Bemisia tabaci, Trialeurodes vaporariorum） <a class="header-anchor" href="#_7-コナジラミ類-whiteflies-bemisia-tabaci-trialeurodes-vaporariorum" aria-label="Permalink to &quot;7. コナジラミ類（Whiteflies: Bemisia tabaci, Trialeurodes vaporariorum）&quot;">​</a></h2><h3 id="加害の特性と被害症状-6" tabindex="-1">加害の特性と被害症状 <a class="header-anchor" href="#加害の特性と被害症状-6" aria-label="Permalink to &quot;加害の特性と被害症状&quot;">​</a></h3><ul><li>小型白色の蛾状昆虫。幼虫は葉裏に鱗状で付着。</li><li>吸汁・ウイルス媒介、葉の黄変、生育抑制、すす病発生が見られる[4][6]。</li></ul><h3 id="加害ステージ-6" tabindex="-1">加害ステージ <a class="header-anchor" href="#加害ステージ-6" aria-label="Permalink to &quot;加害ステージ&quot;">​</a></h3><ul><li>幼虫・成虫とも加害、ウイルス伝播能力も持つ[4]。</li></ul><h3 id="現場での識別・診断-6" tabindex="-1">現場での識別・診断 <a class="header-anchor" href="#現場での識別・診断-6" aria-label="Permalink to &quot;現場での識別・診断&quot;">​</a></h3><ul><li>朝方葉を揺すって白い虫の群れを確認。</li><li>葉裏に鱗状幼虫、蜜露・すす病の確認。</li></ul><hr><h2 id="_8-ミナミキイロアザミウマ他スリップス類-thrips-frankliniella-occidentalis-他" tabindex="-1">8. ミナミキイロアザミウマ他スリップス類（Thrips: Frankliniella occidentalis 他） <a class="header-anchor" href="#_8-ミナミキイロアザミウマ他スリップス類-thrips-frankliniella-occidentalis-他" aria-label="Permalink to &quot;8. ミナミキイロアザミウマ他スリップス類（Thrips: Frankliniella occidentalis 他）&quot;">​</a></h2><h3 id="加害の特性と被害症状-7" tabindex="-1">加害の特性と被害症状 <a class="header-anchor" href="#加害の特性と被害症状-7" aria-label="Permalink to &quot;加害の特性と被害症状&quot;">​</a></h3><ul><li>極小で細長い昆虫、加害時葉裏に銀白色のスジ・小斑点、黒い排泄物痕が出る[4]。</li><li>葉先が縮れる、株全体の生育不良が生じる。</li><li>一部トスポウイルス媒介も（ジャガイモでの頻度は限定的）。</li></ul><h3 id="加害ステージ-7" tabindex="-1">加害ステージ <a class="header-anchor" href="#加害ステージ-7" aria-label="Permalink to &quot;加害ステージ&quot;">​</a></h3><ul><li>成虫・幼虫ともに吸汁加害[4]。</li></ul><h3 id="現場での識別・診断-7" tabindex="-1">現場での識別・診断 <a class="header-anchor" href="#現場での識別・診断-7" aria-label="Permalink to &quot;現場での識別・診断&quot;">​</a></h3><ul><li>葉裏の銀白色の斑や斑点に注目。活発に動く小昆虫を確認。</li></ul><hr><h2 id="その他の重要害虫・地域的発生例" tabindex="-1">その他の重要害虫・地域的発生例 <a class="header-anchor" href="#その他の重要害虫・地域的発生例" aria-label="Permalink to &quot;その他の重要害虫・地域的発生例&quot;">​</a></h2><ul><li><strong>ハモグリバエ類（Leafminers: Liriomyza spp.）</strong><br> 幼虫が葉肉内に白色の曲がりくねった食痕を作る。被害葉はやや光沢あり、収量減原因[4]。</li><li><strong>大型イモムシ・ケラ類（Blister beetles, hornworms, mole crickets “ケラ”）</strong><br> 一部地域や年で発生し、塊茎や根部の直接被害・地中のトンネル形成などで問題となる[2][3][4]。</li></ul><hr><h2 id="害虫管理・圃場診断の基本ポイント" tabindex="-1">害虫管理・圃場診断の基本ポイント <a class="header-anchor" href="#害虫管理・圃場診断の基本ポイント" aria-label="Permalink to &quot;害虫管理・圃場診断の基本ポイント&quot;">​</a></h2><ul><li>被害発見の初動が重要であり、「加害害虫＋特徴的被害痕＋発生時期」で現場診断を行う。</li><li>フェロモントラップ、定期的スカウティング、定量的基準値での防除判断が推奨される。</li><li>防除法は、輪作・被害残渣除去・健全種イモ利用などの耕種的手法、天敵利用やバイオ農薬、薬剤の輪番使用（薬剤抵抗性注意）が標準[3][4][7]。</li><li>日本語および英語の農業ハンドブックでは、初期の徹底抜き取り・健康な種イモの重要性も繰り返し強調されている[2][3][5]。</li></ul><hr><h2 id="参考文献・情報源" tabindex="-1">参考文献・情報源 <a class="header-anchor" href="#参考文献・情報源" aria-label="Permalink to &quot;参考文献・情報源&quot;">​</a></h2><p>[1] Tecia solanivora (Guatemalan Potato Moth) — CAPS Datasheet, Purdue University: <a href="https://caps.ceris.purdue.edu/tecia-solanivora-caps-datasheet-pdf/" target="_blank" rel="noopener noreferrer">https://caps.ceris.purdue.edu/tecia-solanivora-caps-datasheet-pdf/</a><br> [2] やまむファーム—ジャガイモの病気と害虫｜症状の特徴と防除方法: <a href="https://ymmfarm.com/cultivation/pest/by-veg/potato-pests/" target="_blank" rel="noopener noreferrer">https://ymmfarm.com/cultivation/pest/by-veg/potato-pests/</a><br> [3] 防除ハンドブック ジャガイモの病害虫: <a href="https://boujo.net/handbook-18" target="_blank" rel="noopener noreferrer">https://boujo.net/handbook-18</a><br> [4] Potato Field Guide: Insects, Diseases and Defects — Ontario Ministry of Agriculture and Food: <a href="https://spudsmart.com/wp-content/uploads/2022/06/Potato-Field-Guide.pdf" target="_blank" rel="noopener noreferrer">https://spudsmart.com/wp-content/uploads/2022/06/Potato-Field-Guide.pdf</a><br> [5] 知っておきたいジャガイモの病気7選｜症状や発生原因について: <a href="https://chiakifarm.com/spring_vegetables/potato_pest/" target="_blank" rel="noopener noreferrer">https://chiakifarm.com/spring_vegetables/potato_pest/</a><br> [6] Tecia solanivora (potato tuber moth) — CABI Compendium: <a href="https://www.cabidigitallibrary.org/doi/full/10.1079/cabicompendium.52956" target="_blank" rel="noopener noreferrer">https://www.cabidigitallibrary.org/doi/full/10.1079/cabicompendium.52956</a><br> [7] Integrated Pest Management of Potato (Solanum tuberosum L) — OMICS International: <a href="https://www.omicsonline.org/open-access/integrated-pest-management-of-potato-solanum-tuberosum-l-131752.html" target="_blank" rel="noopener noreferrer">https://www.omicsonline.org/open-access/integrated-pest-management-of-potato-solanum-tuberosum-l-131752.html</a></p>',86)])])}const m=e(t,[["render",o]]);export{p as __pageData,m as default};
