import{_ as e,c as o,o as l,ag as t}from"./chunks/framework.BADI-Nii.js";const m=JSON.parse('{"title":"読みにくい・統一されていない箇所のリスト","description":"","frontmatter":{},"headers":[],"relativePath":"../research_reports/読みにくい・統一されていない箇所リスト.md","filePath":"../research_reports/読みにくい・統一されていない箇所リスト.md"}'),i={name:"../research_reports/読みにくい・統一されていない箇所リスト.md"};function r(h,a,d,s,c,n){return l(),o("div",null,[...a[0]||(a[0]=[t('<h1 id="読みにくい・統一されていない箇所のリスト" tabindex="-1">読みにくい・統一されていない箇所のリスト <a class="header-anchor" href="#読みにくい・統一されていない箇所のリスト" aria-label="Permalink to &quot;読みにくい・統一されていない箇所のリスト&quot;">​</a></h1><h2 id="調査日時" tabindex="-1">調査日時 <a class="header-anchor" href="#調査日時" aria-label="Permalink to &quot;調査日時&quot;">​</a></h2><p>2024年（全体精査実施時点）</p><h2 id="調査対象" tabindex="-1">調査対象 <a class="header-anchor" href="#調査対象" aria-label="Permalink to &quot;調査対象&quot;">​</a></h2><p>research_reports配下の全Markdownファイル</p><hr><h2 id="_1-タイトル・見出しの統一" tabindex="-1">1. タイトル・見出しの統一 <a class="header-anchor" href="#_1-タイトル・見出しの統一" aria-label="Permalink to &quot;1. タイトル・見出しの統一&quot;">​</a></h2><h3 id="問題点" tabindex="-1">問題点 <a class="header-anchor" href="#問題点" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>タイトルの形式が統一されていない</strong><ul><li><code>tomato</code>: 「トマト（Solanum lycopersicum）各成長ステージ別温度要件の総合まとめ」</li><li><code>carrot</code>: 「人参（にんじん）の成長ステージ別温度要件の総合整理」</li><li><code>potato</code>: 「じゃがいも（ばれいしょ）の各成長ステージごとの温度要件：総合まとめ」</li><li><code>lettuce</code>: 「レタス（Lactuca sativa）の成長ステージ別・温度要件総括」</li><li><code>eggplant</code>: 「ナス（茄子）の各成長ステージごとの温度要件総合まとめ」</li><li><code>bell_pepper</code>: 「ピーマン（Capsicum annuum）の各成長ステージ別 温度要件まとめ」</li><li><code>pumpkin</code>: 「かぼちゃ（パンプキン）の各成長ステージごとの温度要件：総合サマリー」</li><li><code>cucumber</code>: タイトル形式が異なる可能性</li></ul></li></ul><h3 id="推奨統一形式" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>「[作物名]（[学名]）の各成長ステージごとの温度要件：総合まとめ」</strong></p><hr><h2 id="_2-セクション見出しの統一" tabindex="-1">2. セクション見出しの統一 <a class="header-anchor" href="#_2-セクション見出しの統一" aria-label="Permalink to &quot;2. セクション見出しの統一&quot;">​</a></h2><h3 id="問題点-1" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-1" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><p><strong>「まとめ」セクションの表記が統一されていない</strong></p><ul><li>ほとんどのファイル: 「## まとめ」</li><li><code>potato</code>: 「## まとめ」（修正済み）</li><li><code>lettuce</code>: 「# レタス（Lactuca sativa）の成長ステージ別・温度要件総括」（タイトルに「総括」）</li></ul></li><li><p><strong>表の見出しが統一されていない</strong></p><ul><li><code>carrot</code>: 「## ステージごとの温度要件一覧表」</li><li><code>potato</code>: 「## 成長ステージ別温度パラメータまとめ表」</li><li><code>bell_pepper</code>: 「## まとめ表（主要温度パラメータ一覧）」</li><li><code>lettuce</code>: 「## 参考温度パラメータ早見表」</li><li><code>tomato</code>: 「## 2. 成長ステージ別：温度要件の一覧表」</li></ul></li></ul><h3 id="推奨統一形式-1" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-1" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>「## 成長ステージ別温度パラメータ一覧表」</strong> または <strong>「## 温度要件一覧表」</strong></p><hr><h2 id="_3-パラメータ名の表記の統一" tabindex="-1">3. パラメータ名の表記の統一 <a class="header-anchor" href="#_3-パラメータ名の表記の統一" aria-label="Permalink to &quot;3. パラメータ名の表記の統一&quot;">​</a></h2><h3 id="問題点-2" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-2" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><p><strong>optimal_min/optimal_maxの表記が統一されていない</strong></p><ul><li><code>potato</code>: 「optimal_min（最適最低温度）」「optimal_max（最適最高温度）」（個別に記載）</li><li><code>carrot</code>: 「optimal_min（最適最低温度）」「optimal_max（最適最高温度）」（個別に記載）</li><li><code>tomato</code>: 「optimal_min, optimal_max（最適最低温度・最適最高温度）」（まとめて記載）</li><li><code>lettuce</code>: 「optimal_min / optimal_max」（スラッシュ区切り、日本語説明なし）</li><li><code>bell_pepper</code>: 「Optimal_min / Optimal_max」（大文字、スラッシュ区切り）</li><li><code>eggplant</code>: 表内では「optimal_min」「optimal_max」（小文字、個別）</li></ul></li><li><p><strong>表内でのパラメータ名の表記が統一されていない</strong></p><ul><li><code>potato</code>: 「optimal_min」「optimal_max」「base_temp」「low_stress」「high_stress」「max_temp」</li><li><code>carrot</code>: 「optimal_min」「optimal_max」「base_temperature」「low_stress_threshold」「high_stress_threshold」「max_temperature」</li><li><code>tomato</code>: 「optimal_min」「optimal_max」「base_temperature」「low_stress_threshold」「high_stress_threshold」「max_temperature」</li></ul></li></ul><h3 id="推奨統一形式-2" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-2" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>本文中</strong>: 「optimal_min（最適最低温度）」「optimal_max（最適最高温度）」（個別に記載、小文字） <strong>表内</strong>: 「optimal_min」「optimal_max」「base_temperature」「low_stress_threshold」「high_stress_threshold」「max_temperature」「frost_threshold」「sterility_risk_threshold」（完全なパラメータ名を使用）</p><hr><h2 id="_4-説明文の表現の統一" tabindex="-1">4. 説明文の表現の統一 <a class="header-anchor" href="#_4-説明文の表現の統一" aria-label="Permalink to &quot;4. 説明文の表現の統一&quot;">​</a></h2><h3 id="問題点-3" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-3" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><p><strong>「説明」と「解説」の使い分けが統一されていない</strong></p><ul><li><code>potato</code>: 「説明」（各ステージの説明に使用）</li><li><code>carrot</code>: 「各ステージごとの解説」（セクション見出し）</li><li><code>lettuce</code>: 「各パラメータ解説」（セクション見出し）</li></ul></li><li><p><strong>「備考」と「注記」の使い分けが統一されていない</strong></p><ul><li><code>potato</code>: 「備考」（補足情報に使用）</li><li><code>bell_pepper</code>: 「備考」（補足情報に使用）</li><li><code>lettuce</code>: 「注記」（補足情報に使用）</li><li><code>carrot</code>: 「注記」（概要セクションで使用）</li></ul></li></ul><h3 id="推奨統一形式-3" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-3" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>「説明」</strong>（パラメータやステージの説明に使用）、<strong>「備考」</strong>（補足情報に使用）</p><hr><h2 id="_5-概要セクションの表現の統一" tabindex="-1">5. 概要セクションの表現の統一 <a class="header-anchor" href="#_5-概要セクションの表現の統一" aria-label="Permalink to &quot;5. 概要セクションの表現の統一&quot;">​</a></h2><h3 id="問題点-4" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-4" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>「本報告」の使い方が統一されていない</strong><ul><li><code>potato</code>: 「本報告は、じゃがいも（Solanum tuberosum L.）の各成長ステージで異なる温度要件を整理したものです。」</li><li><code>carrot</code>: 「本報告は、人参（Daucus carota L.）の主要成長ステージごとに必要な温度要件を...整理し、...まとめます。」</li><li><code>tomato</code>: 「本報告では、トマト（Solanum lycopersicum）の栽培における温度要件を、...整理しました。」</li><li><code>lettuce</code>: 「本報告は、レタス（Lactuca sativa）の各成長ステージ（...）ごとの温度要件について、...整理したものです。」</li><li><code>bell_pepper</code>: 「本報告は、ピーマンの主要な成長ステージ（...）の定義と、それぞれの温度要件（...）について、...整理したものである。」</li></ul></li></ul><h3 id="推奨統一形式-4" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-4" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>「本報告は、[作物名]（[学名]）の各成長ステージごとの温度要件について、...整理したものです。」</strong></p><hr><h2 id="_6-表の形式の統一" tabindex="-1">6. 表の形式の統一 <a class="header-anchor" href="#_6-表の形式の統一" aria-label="Permalink to &quot;6. 表の形式の統一&quot;">​</a></h2><h3 id="問題点-5" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-5" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><p><strong>表の列名が統一されていない</strong></p><ul><li><code>potato</code>: 「成長ステージ」「optimal_min」「optimal_max」「base_temp」「low_stress」「high_stress」「max_temp」「frost_threshold」「sterility_risk_threshold」</li><li><code>carrot</code>: 「成長ステージ」「optimal_min」「optimal_max」「base_temperature」「low_stress_threshold」「high_stress_threshold」「max_temperature」「frost_threshold」「sterility_risk_threshold（開花のみ）」「参考文献」</li><li><code>tomato</code>: 「成長ステージ」「optimal_min」「optimal_max」「base_temperature」「low_stress_threshold」「high_stress_threshold」「max_temperature」「frost_threshold」「sterility_risk_threshold」</li><li><code>eggplant</code>: 「成長ステージ」「optimal_min」「optimal_max」「base_temperature」「low_stress_threshold」「high_stress_threshold」「max_temperature」「frost_threshold」「sterility_risk_threshold (開花期のみ)」「参考文献」</li></ul></li><li><p><strong>表の最後の列（参考文献）の有無が統一されていない</strong></p><ul><li><code>carrot</code>: 参考文献列あり</li><li><code>eggplant</code>: 参考文献列あり</li><li><code>potato</code>: 参考文献列なし</li><li><code>tomato</code>: 参考文献列なし</li></ul></li></ul><h3 id="推奨統一形式-5" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-5" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>表の列名</strong>: 「成長ステージ」「optimal_min」「optimal_max」「base_temperature」「low_stress_threshold」「high_stress_threshold」「max_temperature」「frost_threshold」「sterility_risk_threshold（開花期のみ）」「参考文献」（すべての表に参考文献列を追加）</p><hr><h2 id="_7-パラメータ定義セクションの統一" tabindex="-1">7. パラメータ定義セクションの統一 <a class="header-anchor" href="#_7-パラメータ定義セクションの統一" aria-label="Permalink to &quot;7. パラメータ定義セクションの統一&quot;">​</a></h2><h3 id="問題点-6" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-6" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>パラメータ定義セクションの有無・形式が統一されていない</strong><ul><li><code>carrot</code>: 「## 各パラメータの定義」セクションあり</li><li><code>tomato</code>: 「## 1. パラメータ定義の再掲」セクションあり</li><li><code>potato</code>: パラメータ定義セクションなし（各ステージで個別に説明）</li><li><code>lettuce</code>: 「## 各パラメータ解説と参考文献補足」セクションあり</li><li><code>eggplant</code>: 「#### パラメータ定義に関する注意」セクションあり</li></ul></li></ul><h3 id="推奨統一形式-6" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-6" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>「## 各パラメータの定義」</strong> セクションを全ファイルに追加（または削除して統一）</p><hr><h2 id="_8-ステージ名の表記の統一" tabindex="-1">8. ステージ名の表記の統一 <a class="header-anchor" href="#_8-ステージ名の表記の統一" aria-label="Permalink to &quot;8. ステージ名の表記の統一&quot;">​</a></h2><h3 id="問題点-7" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-7" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>ステージ名の表記が統一されていない</strong><ul><li><code>potato</code>: 「発芽（Sprouting）」「出芽（Emergence）」「栄養成長（Vegetative Growth）」「塊茎形成開始（Tuber Initiation）」「塊茎肥大（Tuber Bulking）」「開花（Flowering）」</li><li><code>carrot</code>: 「発芽」「葉茎伸長・栄養成長」「根部肥大」「開花・抽苔」</li><li><code>tomato</code>: 「発芽～苗期」「栄養成長期」「開花・結実（生殖期）」「果実肥大・成熟期」</li><li><code>lettuce</code>: 「発芽期（Germination）」「幼苗期（Seedling）」「生育・結球期」「抽苔・開花期」</li><li><code>bell_pepper</code>: 「発芽期」「育苗期」「生育（栽培）期」「開花期」「結実・肥大期」「成熟・着色期」</li></ul></li></ul><h3 id="推奨統一形式-7" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-7" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>各作物の特性に応じたステージ名を使用</strong>（統一は困難だが、表記形式は統一：日本語名（英語名）の形式）</p><hr><h2 id="_9-数値表記の統一" tabindex="-1">9. 数値表記の統一 <a class="header-anchor" href="#_9-数値表記の統一" aria-label="Permalink to &quot;9. 数値表記の統一&quot;">​</a></h2><h3 id="問題点-8" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-8" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><p><strong>温度範囲の表記が統一されていない</strong></p><ul><li><code>potato</code>: 「4°C」「20°C」「約4°C」「約30°C」</li><li><code>carrot</code>: 「15°C」「25°C」「5～6°C」「約3°C」</li><li><code>tomato</code>: 「18°C」「29°C」「10°C」「12–13°C」</li><li><code>lettuce</code>: 「16°C」「22°C」「約7°C」</li></ul></li><li><p><strong>範囲の表記が統一されていない</strong></p><ul><li><code>potato</code>: 「7–10°C」（ハイフン）</li><li><code>carrot</code>: 「5～6°C」（波ダッシュ）</li><li><code>tomato</code>: 「15–17°C(夜)」（ハイフン）</li><li><code>lettuce</code>: 「10°C」「20°C」（個別に記載）</li></ul></li></ul><h3 id="推奨統一形式-8" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-8" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>「～」（波ダッシュ）を使用</strong>（例：「5～6°C」「15～25°C」）</p><hr><h2 id="_10-参考文献セクションの統一" tabindex="-1">10. 参考文献セクションの統一 <a class="header-anchor" href="#_10-参考文献セクションの統一" aria-label="Permalink to &quot;10. 参考文献セクションの統一&quot;">​</a></h2><h3 id="問題点-9" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-9" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><p><strong>参考文献セクションの見出しが統一されていない</strong></p><ul><li>ほとんどのファイル: 「## 参考文献」</li><li>一部のファイル: 「### Sources」（セクション名として）</li></ul></li><li><p><strong>参考文献の表記形式が統一されていない</strong></p><ul><li>一部のファイル: 「### Sources」の下に参考文献リスト</li><li>一部のファイル: 「## 参考文献」の下に参考文献リスト</li></ul></li></ul><h3 id="推奨統一形式-9" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-9" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>「## 参考文献」</strong> を統一（「### Sources」は削除または「## 参考文献」に変更）</p><hr><h2 id="_11-その他の読みにくい箇所" tabindex="-1">11. その他の読みにくい箇所 <a class="header-anchor" href="#_11-その他の読みにくい箇所" aria-label="Permalink to &quot;11. その他の読みにくい箇所&quot;">​</a></h2><h3 id="問題点-10" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-10" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><p><strong>長文の説明が読みにくい</strong></p><ul><li><code>potato</code>: 「一般的な証拠は28°C超で花粉不稔と花の中絶のリスクを示唆していますが、明確な花粉不稔温度は最近の査読付き文献で明記なし。」（長文）</li><li><code>carrot</code>: 「開花期における「高温不稔の発生温度」はイネ・麦などの穀類では詳細な品種間差も含め定義されていますが、人参に関してこれに該当する閾値を明示した日本語・英語文献は皆無です（抽苔や花芽分化の温度処理の研究が中心）。」（長文）</li></ul></li><li><p><strong>表内の値の表記が統一されていない</strong></p><ul><li><code>potato</code>: 「—」（ダッシュ）で未定義を表す</li><li><code>carrot</code>: 「明記なし」で未定義を表す</li><li><code>tomato</code>: 「null」で未定義を表す</li></ul></li></ul><h3 id="推奨統一形式-10" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-10" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>表内の未定義値</strong>: 「—」（ダッシュ）に統一</p><hr><h2 id="_12-英語表記の統一" tabindex="-1">12. 英語表記の統一 <a class="header-anchor" href="#_12-英語表記の統一" aria-label="Permalink to &quot;12. 英語表記の統一&quot;">​</a></h2><h3 id="問題点-11" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-11" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>英語のステージ名の表記が統一されていない</strong><ul><li><code>potato</code>: 「Sprouting」「Emergence」「Vegetative Growth」「Tuber Initiation」「Tuber Bulking」「Flowering」（大文字で始まる）</li><li><code>lettuce</code>: 「Germination」「Seedling」（大文字で始まる）</li><li><code>carrot</code>: 英語表記なし</li><li><code>tomato</code>: 英語表記なし</li></ul></li></ul><h3 id="推奨統一形式-11" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-11" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>英語表記は括弧内に記載</strong>（例：「発芽（Sprouting）」）</p><hr><h2 id="_13-単位表記の統一" tabindex="-1">13. 単位表記の統一 <a class="header-anchor" href="#_13-単位表記の統一" aria-label="Permalink to &quot;13. 単位表記の統一&quot;">​</a></h2><h3 id="問題点-12" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-12" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>温度単位は統一済み</strong>（°C）</li><li><strong>GDD単位の表記が統一されていない</strong><ul><li><code>potato</code>: 「°C・日」（修正済み）</li><li><code>spinach</code>: 「°C日」（修正が必要）</li><li><code>onion</code>: 「°C日」（修正が必要）</li></ul></li></ul><h3 id="推奨統一形式-12" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-12" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>「°C・日」</strong> に統一</p><hr><h2 id="_14-表内の値の表記の統一" tabindex="-1">14. 表内の値の表記の統一 <a class="header-anchor" href="#_14-表内の値の表記の統一" aria-label="Permalink to &quot;14. 表内の値の表記の統一&quot;">​</a></h2><h3 id="問題点-13" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-13" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>表内の未定義値の表記が統一されていない</strong><ul><li><code>potato</code>: 「—」（ダッシュ）で未定義を表す</li><li><code>carrot</code>: 「明記なし」で未定義を表す</li><li><code>tomato</code>: 「null」で未定義を表す</li><li><code>cabbage</code>: 「-」（ハイフン）で未定義を表す</li><li><code>onion</code>: 「—」（ダッシュ）で未定義を表す</li><li><code>eggplant</code>: 「–」（enダッシュ）で未定義を表す</li></ul></li></ul><h3 id="推奨統一形式-13" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-13" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>表内の未定義値</strong>: 「—」（emダッシュ）に統一</p><hr><h2 id="_15-数値範囲の表記の統一" tabindex="-1">15. 数値範囲の表記の統一 <a class="header-anchor" href="#_15-数値範囲の表記の統一" aria-label="Permalink to &quot;15. 数値範囲の表記の統一&quot;">​</a></h2><h3 id="問題点-14" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-14" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>数値範囲の表記が統一されていない</strong><ul><li><code>potato</code>: 「7–10°C」（enダッシュ）</li><li><code>carrot</code>: 「5～6°C」（波ダッシュ）</li><li><code>tomato</code>: 「12–13°C」「15–17°C(夜)」（enダッシュ）</li><li><code>eggplant</code>: 「20–25°C」「22–25°C」（enダッシュ）</li><li><code>cucumber</code>: 「25〜30°C」（波ダッシュ）</li><li><code>onion</code>: 「7–8°C」「5–7°C」（enダッシュ）</li></ul></li></ul><h3 id="推奨統一形式-14" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-14" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>「～」（波ダッシュ）を使用</strong>（例：「5～6°C」「15～25°C」）</p><hr><h2 id="_16-パラメータ名の大文字-小文字の統一" tabindex="-1">16. パラメータ名の大文字/小文字の統一 <a class="header-anchor" href="#_16-パラメータ名の大文字-小文字の統一" aria-label="Permalink to &quot;16. パラメータ名の大文字/小文字の統一&quot;">​</a></h2><h3 id="問題点-15" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-15" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>パラメータ名の大文字/小文字が統一されていない</strong><ul><li><code>potato</code>: 「optimal_min」「optimal_max」（小文字）</li><li><code>carrot</code>: 「optimal_min」「optimal_max」（小文字）</li><li><code>tomato</code>: 「optimal_min, optimal_max」（小文字、カンマ区切り）</li><li><code>bell_pepper</code>: 「Optimal_min / Optimal_max」（大文字、スラッシュ区切り）</li><li><code>lettuce</code>: 「optimal_min / optimal_max」（小文字、スラッシュ区切り）</li></ul></li></ul><h3 id="推奨統一形式-15" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-15" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>小文字に統一</strong>（例：「optimal_min」「optimal_max」）</p><hr><h2 id="_17-表内のパラメータ名の省略形の統一" tabindex="-1">17. 表内のパラメータ名の省略形の統一 <a class="header-anchor" href="#_17-表内のパラメータ名の省略形の統一" aria-label="Permalink to &quot;17. 表内のパラメータ名の省略形の統一&quot;">​</a></h2><h3 id="問題点-16" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-16" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>表内でのパラメータ名の省略が統一されていない</strong><ul><li><code>potato</code>: 「base_temp」「low_stress」「high_stress」「max_temp」（省略形）</li><li><code>carrot</code>: 「base_temperature」「low_stress_threshold」「high_stress_threshold」「max_temperature」（完全形）</li><li><code>tomato</code>: 「base_temperature」「low_stress_threshold」「high_stress_threshold」「max_temperature」（完全形）</li><li><code>onion</code>: 「base_temp」「low_stress」「high_stress」「max_temp」（省略形）</li></ul></li></ul><h3 id="推奨統一形式-16" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-16" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>完全なパラメータ名を使用</strong>（例：「base_temperature」「low_stress_threshold」「high_stress_threshold」「max_temperature」）</p><hr><h2 id="_18-コメント・注釈の表記の統一" tabindex="-1">18. コメント・注釈の表記の統一 <a class="header-anchor" href="#_18-コメント・注釈の表記の統一" aria-label="Permalink to &quot;18. コメント・注釈の表記の統一&quot;">​</a></h2><h3 id="問題点-17" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-17" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>コメント・注釈の表記が統一されていない</strong><ul><li><code>tomato</code>: 「_コメント：...」（イタリック）</li><li><code>tomato</code>: 「（*注：...）」（括弧内に注）</li><li><code>cabbage</code>: 「*注：...」（アスタリスク）</li><li><code>onion</code>: 「＊...」（全角アスタリスク）</li></ul></li></ul><h3 id="推奨統一形式-17" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-17" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p>*<em>「<em>注：...」</em></em> に統一（表の下などで使用）</p><hr><h2 id="_19-参考文献セクションの重複" tabindex="-1">19. 参考文献セクションの重複 <a class="header-anchor" href="#_19-参考文献セクションの重複" aria-label="Permalink to &quot;19. 参考文献セクションの重複&quot;">​</a></h2><h3 id="問題点-18" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-18" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>参考文献セクションが重複しているファイルがある</strong><ul><li><code>bell_pepper</code>: 「## 参考文献」と「### Sources」の両方がある</li><li><code>lettuce</code>: 「## 参考文献」と「### Sources」の両方がある</li></ul></li></ul><h3 id="推奨統一形式-18" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-18" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>「## 参考文献」のみに統一</strong>（「### Sources」は削除）</p><hr><h2 id="_20-セクション番号の統一" tabindex="-1">20. セクション番号の統一 <a class="header-anchor" href="#_20-セクション番号の統一" aria-label="Permalink to &quot;20. セクション番号の統一&quot;">​</a></h2><h3 id="問題点-19" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-19" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>セクション番号の有無が統一されていない</strong><ul><li><code>tomato</code>: 「## 1. パラメータ定義の再掲」「## 2. 成長ステージ別：温度要件の一覧表」「## 3. 各成長ステージ別の詳細解説」（番号あり）</li><li><code>cucumber</code>: 「## 1. 発芽期の温度要件」「## 2. 苗期の温度要件」（番号あり）</li><li><code>potato</code>: 「### 1. 発芽（Sprouting）」「### 2. 出芽（Emergence）」（番号あり）</li><li><code>carrot</code>: 「## 各パラメータの定義」「## ステージごとの温度要件一覧表」（番号なし）</li><li><code>lettuce</code>: 「## 各成長ステージ別 温度パラメータ一覧」（番号なし）</li></ul></li></ul><h3 id="推奨統一形式-19" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-19" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>番号なしに統一</strong>（または、すべて番号ありに統一）</p><hr><h2 id="_21-表のスタイルの統一" tabindex="-1">21. 表のスタイルの統一 <a class="header-anchor" href="#_21-表のスタイルの統一" aria-label="Permalink to &quot;21. 表のスタイルの統一&quot;">​</a></h2><h3 id="問題点-20" tabindex="-1">問題点 <a class="header-anchor" href="#問題点-20" aria-label="Permalink to &quot;問題点&quot;">​</a></h3><ul><li><strong>表のスタイルが統一されていない</strong><ul><li><code>onion</code>: 「|:-----------😐:-----------😐」（中央揃え記号あり）</li><li>他のファイル: 中央揃え記号なし</li></ul></li></ul><h3 id="推奨統一形式-20" tabindex="-1">推奨統一形式 <a class="header-anchor" href="#推奨統一形式-20" aria-label="Permalink to &quot;推奨統一形式&quot;">​</a></h3><p><strong>中央揃え記号は使用しない</strong>（または、すべて使用）</p><hr><h2 id="優先度別の統一推奨事項" tabindex="-1">優先度別の統一推奨事項 <a class="header-anchor" href="#優先度別の統一推奨事項" aria-label="Permalink to &quot;優先度別の統一推奨事項&quot;">​</a></h2><h3 id="優先度-高-読みやすさに大きく影響" tabindex="-1">優先度：高（読みやすさに大きく影響） <a class="header-anchor" href="#優先度-高-読みやすさに大きく影響" aria-label="Permalink to &quot;優先度：高（読みやすさに大きく影響）&quot;">​</a></h3><ol><li><strong>表の形式の統一</strong>（列名、参考文献列の有無、パラメータ名の完全形使用）</li><li><strong>パラメータ名の表記の統一</strong>（本文中：小文字、括弧内に日本語説明。表内：完全なパラメータ名）</li><li><strong>表内の未定義値の表記統一</strong>（「—」（emダッシュ）に統一）</li><li><strong>セクション見出しの統一</strong>（「まとめ」「一覧表」など）</li><li><strong>数値範囲の表記統一</strong>（「～」（波ダッシュ）に統一）</li></ol><h3 id="優先度-中-統一感に影響" tabindex="-1">優先度：中（統一感に影響） <a class="header-anchor" href="#優先度-中-統一感に影響" aria-label="Permalink to &quot;優先度：中（統一感に影響）&quot;">​</a></h3><ol start="6"><li><strong>概要セクションの表現の統一</strong>（「本報告は」の使い方）</li><li><strong>説明文の表現の統一</strong>（「説明」「備考」の使い分け）</li><li><strong>タイトルの形式の統一</strong></li><li><strong>参考文献セクションの統一</strong>（「Sources」→「参考文献」、重複削除）</li><li><strong>パラメータ名の大文字/小文字の統一</strong>（小文字に統一）</li></ol><h3 id="優先度-低-細かい統一" tabindex="-1">優先度：低（細かい統一） <a class="header-anchor" href="#優先度-低-細かい統一" aria-label="Permalink to &quot;優先度：低（細かい統一）&quot;">​</a></h3><ol start="11"><li><strong>パラメータ定義セクションの統一</strong>（有無の統一）</li><li><strong>GDD単位の表記統一</strong>（「°C日」→「°C・日」）</li><li><strong>コメント・注釈の表記統一</strong>（「*注：...」に統一）</li><li><strong>セクション番号の統一</strong>（番号なしに統一、またはすべて番号ありに統一）</li><li><strong>表のスタイルの統一</strong>（中央揃え記号の有無）</li></ol><hr><h2 id="注意事項" tabindex="-1">注意事項 <a class="header-anchor" href="#注意事項" aria-label="Permalink to &quot;注意事項&quot;">​</a></h2><ul><li>内容は一切変更せず、表記のみを統一する</li><li>文脈によって適切な表記が異なる場合は、統一を強制しない</li><li>各作物の特性に応じた表現は維持する</li></ul>',142)])])}const _=e(i,[["render",r]]);export{m as __pageData,_ as default};
